import React from "react";
import SingleMovie from "./SingleMovie";

export default class Search extends React.Component {
    constructor() {
        super();
        this.state = {
            movies: require('./moviedata.json'),
            value: '',
            data: ' '
        }
    }

    changeHandler = (e) => {
        this.setState({
            value: e.target.value,
            data: this.state.movies.map(i => <div>{i.name}</div>)
        })
    }



    render() {



        // console.log(this.state.movies)

        console.log('data', this.state.data)



        var list = this.state.movies.filter(item => {
            return this.state.value === " " || this.state.value;
        }).map(item => {
            return (
                <div className="movie" key={item.imdbID}>
                    <img src={item.Poster} alt={item.Title} />
                    <p>{item.Title}</p>
                </div>
            )
        })

        return (
            <div>
                <input id="searchbar" onChange={this.changeHandler.bind(this)} type="text" />
                <div>
                    <SingleMovie movie={list} />
                </div>
            </div>
        )
    }
}
