import React from "react";
import Results from "./Results";
import { throwStatement } from "@babel/types";

export default class Search extends React.Component {
    constructor() {
        super();
        this.state = {
            movies: [require('./moviedata.json')],
            value: ''
        }
    }

    changeHandler = (e) => {
        this.setState({
            value: e.target.value
        })
    }



    render() {
        console.log('input value is ', this.state.value);

        var list = this.state.value.filter(item => {
            return (
                this.state.value === " " || this.state.movies.includes(item)
            )
        }).map(item => {
            return (
                <div key={item.imdbID}>
                     <img src={item.Poster} alt={item.Title} />
//               <p>{item.Title}</p>
                </div>
            )
        })



        console.log(list)

        return (
            <div>
                <input id="searchbar" onChange={this.changeHandler.bind(this)} type="text" />

            </div>
        )
    }
}
