import React from "react";
import SingleMovie from "./SingleMovie";

export default class Search extends React.Component {
    constructor() {
        super();
        this.state = {
            movies: require('./moviedata.json'),
            value: ''
        }
    }

    changeHandler = (e) => {
        this.setState({
            value: e.target.value
            // data: this.state.movies.filter(i => { i.Title })
        })
    }



    render() {







        var list = this.state.value.filter(i => {
            return (
                this.state.value === " " || this.state.value.includes(i)
            )
        })
        // .map(item => {
        //     return (
        //         <div className="movie" key={item.imdbID}>
        //             <img src={item.Poster} alt={item.Title} />
        //             <p>{item.Title}</p>
        //         </div>
        //     )
        // })

        return (
            <div>
                <input id="searchbar" onChange={this.changeHandler.bind(this)} type="text" />
                <div>
                    <SingleMovie movie={list} />
                </div>
            </div>
        )
    }
}
